const airtel_open_btn = document.querySelector('.airtel-open-btn');
const airtel_close_btn = document.querySelector('.airtel-close-btn');
const airtel_nav = document.querySelectorAll('.airtel-nav');

airtel_open_btn.addEventListener('click', () => {
	airtel_nav.forEach(nav_el => { nav_el.classList.add('visible') });
});

airtel_close_btn.addEventListener('click', () => {
	airtel_nav.forEach(nav_el => { nav_el.classList.remove('visible') });
});

// GET IN TOUCH COMPONENT
const floating_btn = document.querySelector('.floating-btn');
const close_btn = document.querySelector('.close-btn');
const social_panel_container = document.querySelector('.social-panel-container');

floating_btn.addEventListener('click', () => {
	social_panel_container.classList.toggle('visible')
});

close_btn.addEventListener('click', () => {
	social_panel_container.classList.remove('visible')
});